import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import '../ProductList.css'; // اگه CSS نداری، اینو کامنت کن

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  console.log('ProductList component mounted');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('token');
        console.log('Fetching products with token:', token || 'No token');
        if (!token) {
          setError('لطفاً دوباره وارد شوید');
          return;
        }
        const response = await axios.get('http://127.0.0.1:8000/products', {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log('API response:', response.data);
        setProducts(response.data);
      } catch (err) {
        console.log('API error:', err.message);
        setError(err.response?.data?.detail || 'خطا در دریافت محصولات');
      }
    };
    fetchProducts();
  }, []);

  console.log('Current state - products:', products, 'error:', error);

  if (error) {
    return <div className="error-message">خطا: {error}</div>;
  }
  if (products === null || products === undefined) {
    return <div className="loading-message">در حال بارگذاری...</div>;
  }
  if (!products.length) {
    return <div className="no-products-message">محصولی یافت نشد</div>;
  }

  return (
    <div className="product-list-container">
      <h2>لیست محصولات</h2>
      <table>
        <thead>
          <tr>
            <th>نام محصول</th>
            <th>کد محصول</th>
            <th>کد اضافی</th>
            <th>تاریخ ایجاد</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.product_name}</td>
              <td>{product.product_code}</td>
              <td>{product.additional_code}</td>
              <td>{new Date(product.created_at).toLocaleString('fa-IR')}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductList;
