import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState } from 'react';
import './App.css';

function Login({ setToken }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8000/token', {
        method: 'POST',
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`,
      });
      const data = await response.json();
      if (response.ok) {
        setToken(data.access_token);
        setError('');
      } else {
        // مدیریت خطاهای مختلف
        if (data.detail) {
          setError(typeof data.detail === 'string' ? data.detail : JSON.stringify(data.detail));
        } else {
          setError('خطا در ورود');
        }
      }
    } catch (err) {
      setError('اتصال به سرور ناموفق بود');
    }
  };

  return (
    <div>
      <h2>ورود</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="نام کاربری"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="رمز عبور"
        />
        <button type="submit">ورود</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
}

function GenerateProduct({ token }) {
  const [code, setCode] = useState('');
  const [additionalCode, setAdditionalCode] = useState('');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');

  const handleGenerate = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8000/generate_product', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': `Bearer ${token}`,
        },
        body: `code=${code}&additional_code=${additionalCode}`,
      });
      const data = await response.json();
      if (response.ok) {
        setResult(data.product_name);
        setError('');
      } else {
        setError(data.detail || 'خطا در تولید محصول');
      }
    } catch (err) {
      setError('اتصال به سرور ناموفق بود');
    }
  };

  return (
    <div>
      <h2>تولید محصول</h2>
      <form onSubmit={handleGenerate}>
        <input
          type="text"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="کد 29 رقمی"
        />
        <input
          type="text"
          value={additionalCode}
          onChange={(e) => setAdditionalCode(e.target.value)}
          placeholder="کد اضافی"
        />
        <button type="submit">تولید</button>
        {result && <p>محصول: {result}</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');

  console.log('App.js is rendering, token:', token);

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={!token ? <Login setToken={setToken} /> : <Navigate to="/generate" />}
        />
        <Route
          path="/generate"
          element={token ? <ProductGenerator token={token} /> : <Navigate to="/login" />}
        />
        <Route
          path="/products"
          element={token ? <ProductList token={token} /> : <Navigate to="/login" />}
        />
        <Route path="/" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
